import { Container, Row, Col, Tab } from "react-bootstrap";
import { ProjectCard } from "../components/ProjectCard/ProjectCard";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';
import { useState, useEffect } from 'react';
import axios from 'axios';
import {getProjectCards} from '../services/projectCardService'

export const Projects = () => {
  useEffect(() => {
    fetchProjectCards();
  }, []);

  const fetchProjectCards = async () => {
    const data = await getProjectCards();
    setProjects(data);
  };

  const [projects, setProjects] = useState([]);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/project-cards'); // Update with your backend API URL
        setProjects(response.data);
      } catch (error) {
        console.error("Error fetching project data", error);
      }
    };

    fetchProjects();
  }, []);

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div>
                <h2>Projects</h2>
                <p>
                  Here are some of my projects that I've worked on. You can
                  click on the image to view more details.
                </p>
                <Tab.Container id="projects-tabs" defaultActiveKey="first">
                  <Tab.Pane eventKey="first">
                    <Row>
                      {
                        projects.map((project, index) => (
                          <ProjectCards
                            key={index}
                            title={project.title}
                            description={project.description}
                            imgUrl={project.imageUrl}
                          />
                        ))
                      }
                    </Row>
                  </Tab.Pane>
                </Tab.Container>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="Background" />
    </section>
  );
}
