    <Col size={12} sm={6} md={4}>
      <div className="proj-imgbx">
        <img src={imageUrl} alt={title} />
        <div className="proj-txtx">
          <h4>{title}</h4>
          <span>{description}</span>
          <p></p>
          <Link className = "pass" to = {`/${project._id}`} > see more {'>>'}</Link>
        </div>
      </div>
    </Col>
  )
}
