import React, { useState, useEffect } from 'react';
import { getProjectCards, addProjectCard, updateProjectCard, deleteProjectCard } from '../../services/projectCardService';

export const ProjectCards = () => {
  const [projectCards, setProjectCards] = useState([]);
  const [newCard, setNewCard] = useState({ title: '', description: '', imageUrl: '', link: '' });

  useEffect(() => {
    fetchProjectCards();
  }, []);

  const fetchProjectCards = async () => {
    const data = await getProjectCards();
    setProjectCards(data);
  };

  const handleAddCard = async () => {
    const addedCard = await addProjectCard(newCard);
    setProjectCards([...projectCards, addedCard]);
    setNewCard({ title: '', description: '', imageUrl: '', link: '' });
  };

  const handleUpdateCard = async (id, updatedInfo) => {
    const updatedCard = await updateProjectCard(id, updatedInfo);
    setProjectCards(projectCards.map((card) => (card._id === id ? updatedCard : card)));
  };

  const handleDeleteCard = async (id) => {
    await deleteProjectCard(id);
    setProjectCards(projectCards.filter((card) => card._id !== id));
  };

  return (
    <div>
      <h2>Project Cards</h2>
      <div>
        <input
          type="text"
          placeholder="Title"
          value={newCard.title}
          onChange={(e) => setNewCard({ ...newCard, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newCard.description}
          onChange={(e) => setNewCard({ ...newCard, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Image URL"
          value={newCard.imageUrl}
          onChange={(e) => setNewCard({ ...newCard, imageUrl: e.target.value })}
        />
        <input
          type="text"
          placeholder="Link"
          value={newCard.link}
          onChange={(e) => setNewCard({ ...newCard, link: e.target.value })}
        />
        <button onClick={handleAddCard}>Add Project Card</button>
      </div>

      {projectCards.map((card) => (
        <div key={card._id}>
          <h3>{card.title}</h3>
          <p>{card.description}</p>
          <img src={card.imageUrl} alt={card.title} style={{ width: '100px' }} />
          <p><a href={card.link}>Project Link</a></p>
          <button onClick={() => handleUpdateCard(card._id, { title: 'Updated Title' })}>Edit</button>
          <button onClick={() => handleDeleteCard(card._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

