import './ProjectCard.css'
import { Col } from "react-bootstrap";
import { Link } from 'react-router-dom'
import React, { useState , useEffect} from 'react';
import { getProject  } from  '../../services/projectCardService'

export const ProjectCard = ({ title, description, Hedimage ,  link   }) => {

  const [project, setProject] = useState([]);
  const [error, setError] = useState(null);

   useEffect(() => {
        fetchProject();
      }, []);

    const fetchProject = async () => {
    try {
      const data = await getProject();
      setProject(data);
      data.forEach((proj) => console.log(proj._id));
    } catch (err) {
        console.error("Error fetching project data", err);
      setError("Could not load project details.");
    }
  };
  
  if (error) {
    return <p>{error}</p>;
  }

  if (!project) {
    return <p>Loading...</p>;
  }

  return (
    <Col size={12} sm={6} md={4}>
      <div className="proj-imgbx">
        <img src={Hedimage} alt={title} />
        <div className="proj-txtx">
          <h4>{title}</h4>
          <span>{description}</span>
          <p></p>
          <Link className = "pass" to = "/details" > see more {'>>'}</Link>
        </div>
      </div>
    </Col>
  )
}
