import './Admin.css'
import React, { useState, useEffect } from 'react';
import { addProject, getProject, deleteProject } from '../../services/projectCardService';
import { ProjectCard } from "../ProjectCard/ProjectCard";

export const ProjectCards = () => {
  const [projectCards, setProjectCards] = useState([]);
  const [newCard, setNewCard] = useState({ title: '', description: '', imageUrl: [], Hedimage: '' , demo: '' , video: ''  , github: '' , details: '' });

  useEffect(() => {
    fetchProject();
  }, []);

  const fetchProjectCard = async () => {
    const data = await getProject();
    setProjectCards(data);
  };

  const handleAddCard = async () => {
    const addedCard = await addProject(newCard);
    setProjectCards([...projectCards, addedCard]);
    setNewCard({ title: '', description: '', imageUrl: '', link: '' });
  };

  const handleDeleteCard = async (id) => {
    await deleteProject(id);
    setProjectCards(projectCards.filter((card) => card._id !== id));
  };

  return (
    <div >
      <h2>Project Cards</h2>
      <div>
        <input style={{ marginTop : "20px" , marginBottom : "20px"}}
          type="text"
          placeholder="Title"
          value={newCard.title}
          onChange={(e) => setNewCard({ ...newCard, title: e.target.value })}
        />
        <input style={{ marginTop : "20px" , marginBottom : "20px"}}
          type="text"
          placeholder="details"
          value={newCard.details}
          onChange={(e) => setNewCard({ ...newCard, details: e.target.value })}
        />
        <input style={{ marginTop : "20px" , marginBottom : "20px"}}
          type="text"
          placeholder="github"
          value={newCard.github}
          onChange={(e) => setNewCard({ ...newCard, github: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newCard.description}
          onChange={(e) => setNewCard({ ...newCard, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Hedimage"
          value={newCard.Hedimage}
          onChange={(e) => setNewCard({ ...newCard, Hedimage: e.target.value })}
        />
        <input
          type="text"
          placeholder="video"
          value={newCard.video}
          onChange={(e) => setNewCard({ ...newCard, video: e.target.value })}
        />
        <input
          type="text"
          placeholder="demo"
          value={newCard.demo}
          onChange={(e) => setNewCard({ ...newCard, demo: e.target.value })}
        />
        <input
          type="text"
          placeholder="imageUrl"
          value={newCard.imageUrl}
          onChange={(e) => setNewCard({ ...newCard, imageUrl: e.target.value })}
        />
        <button onClick={handleAddCard}>Add Project Card</button>
      </div>

      {
        projectCards.map((project, card) => (
          <div key={card._id}>
            <ProjectCard
              title={project.title}
              description={project.description}
              imageUrl={project.imageUrl}
              link={project.link}
            />
            <button className = "pass" onClick={() => handleDeleteCard(card._id)}>Delete</button>
            </div>
          ))}
          </div>
  );
};
