import './ProjectCard.css'
import { Col } from "react-bootstrap";
import { Link } from 'react-router-dom'
import React, { useState, useEffect } from 'react';

export const ProjectCard = ({ title, description, imageUrl ,  link }) => {

  const [project, setProject] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const data = await getProject();
        const angularProject = data.find(proj => proj.title === "Angular E-Commerce App");
        if (angularProject) {
          setProject(angularProject);
        } else {
          setError("Project not found.");
        }
      } catch (err) {
        setError("Could not load project.");
      }
    };
    fetchProject();
  }, []);

  if (error) {
    return <p>{error}</p>;
  }

  if (!project) {
    return <p>Loading...</p>;
  }

  return (
    <Col size={12} sm={6} md={4}>
      <div className="proj-imgbx">
        <img src={imageUrl} alt={title} />
        <div className="proj-txtx">
          <h4>{title}</h4>
          <span>{description}</span>
          <p></p>
          <Link className = "pass" to = {`/${link}/${link._id}`} > see more {'>>'}</Link>
        </div>
      </div>
    </Col>
  )
}
