import React, { useState , useEffect} from 'react';
import { useParams } from 'react-router-dom';
import { getProject , getProjectById } from  '../../services/projectCardService'
export const Pages = () => {

    const { id } = useParams();
    const [project, setProject] = useState([]);
    const [error, setError] = useState(null);
    const [zoomedImage, setZoomedImage] = useState(null);

    useEffect(() => {
        const fetchProject = async () => {
        try {
          const data = await getProjectById(id);
          setProject(data);
        } catch (err) {
          setError("Could not load project details.");
        }
      };
      fetchProject();
    }, [id]);

    useEffect(() => {
        fetchProject();
      }, []);
      
    const fetchProject = async () => {
        try{
          const data = await getProject();
          setProject(data);
          if (Array.isArray(data)) {
            setProject(data); 
          } else {
            console.error("Unexpected API response format", data);
            setError("Unexpected data format from API");
          }
        }  catch (err) {
          console.error("Error fetching project data", err);
          setError("Could not load projects.");
        }
    };

    const openZoom = (image) => {
      setZoomedImage(image);
    };
  
    const closeZoom = () => {
      setZoomedImage(null);
    };
  
    const goToHome = () => {
      window.location.href = '/';  
    };
  
    return (
      <div className="banner1">
        <div className="back-to-home" onClick={goToHome}>
         Home
        </div>
        <div className='font'>
          <h1>{project.titl}</h1>
          <p>This is the detailed view of the Angular E-Commerce project.</p>
          <div className="horizontal-slider">
            {/* {images.map((image, index) => (
              <div className="slider-item" key={index}>
                <img
                  className="slider-image"
                  src={image.src}
                  alt={image.alt}
                  onClick={() => openZoom(image)}
                />
                <div className="slider-caption">
                  <h3>{image.caption}</h3>
                </div>
              </div>
            ))} */}
          </div>

          <button onClick={() => window.open(project.github, '_blank')} className="pass"> 
            <span>Show in GitHub {'>>'}</span>
        </button>
        {project.video && (
        <button onClick={() => window.open(project.video, '_blank')} className="pass">
            <span>Watch Video {'>>'}</span>
        </button>
      )}
          {/* <button className="pass" onClick={handleGithubRedirect}>
            <span>Show in GitHub {'>>'}</span>
          </button>
          <p></p>
          <button className="pass" onClick={handleVideo}>
            <span>Watch Video {'>>'}</span>
          </button> */}
        </div>
        {zoomedImage && (
          <div className="zoom-modal" onClick={closeZoom}>
            <span className="close">&times;</span>
            <img className="zoomed-image" src={zoomedImage.src} alt={zoomedImage.alt} />
          </div>
        )}
      </div>
    );
  };
