import React, { useState , useEffect} from 'react';
import { useParams } from 'react-router-dom';
import { getProject , getProjectById } from  '../../services/projectCardService'
export const Pages = () => {

    const { id } = useParams();
    const [project, setProject] = useState([]);
    const [error, setError] = useState(null);
    const [zoomedImage, setZoomedImage] = useState(null);

    useEffect(() => {
        const fetchProject = async () => {
        try {
          const data = await getProjectById(id);
          setProject(data);
        } catch (err) {
          setError("Could not load project details.");
        }
      };
      fetchProject();
    }, [id]);

    // useEffect(() => {
    //     fetchProject();
    //   }, []);
      
    // const fetchProject = async () => {
    //     try{
    //       const data = await getProject();
    //       setProject(data);
    //       if (Array.isArray(data)) {
    //         setProject(data); 
    //       } else {
    //         console.error("Unexpected API response format", data);
    //         setError("Unexpected data format from API");
    //       }
    //     }  catch (err) {
    //       console.error("Error fetching project data", err);
    //       setError("Could not load projects.");
    //     }
    // };

    const openZoom = (image) => {
      setZoomedImage(image);
    };
  
    const closeZoom = () => {
      setZoomedImage(null);
    };
  
    const goToHome = () => {
      window.location.href = '/';  
    };

    if (error) {
        return <p>{error}</p>;
      }
    
      if (!project) {
        return <p>Loading...</p>;
      }
  
    return (
        <div className="banner1">
            <div className="back-to-home" onClick={goToHome}>
                Home
            </div>
        <div className='font'>
        <h1>{project.title}</h1>
        <p>{project.description}</p>
        <div className="horizontal-slider">
        {project.imageUrl.map((src, index) => (
        <div className="slider-item" key={index}>
          <img 
            src={src} 
            alt={`Project image ${index + 1}`}                   
            onClick={() => openZoom(src)}
          />
          </div>
        ))}
      </div>
      <button onClick={() => window.open(project.github, '_blank')}>GitHub</button>
      {project.video && (
        <button onClick={() => window.open(project.video, '_blank')}>Watch Video</button>
      )}
      {project.demo && (
        <button onClick={() => window.open(project.demo, '_blank')}>Live Demo</button>
      )}
      </div>

        {zoomedImage && (
          <div className="zoom-modal" onClick={closeZoom}>
            <span className="close">&times;</span>
            <img className="zoomed-image" src={zoomedImage.src} alt={zoomedImage.alt} />
          </div>
        )}
      </div>
    );
  };
